sys_variable_value.value:
(function execute(inputs, outputs) {
    // Create a GlideRecord object for your employee table
    var gr = new GlideRecord('x_1745159_growhive_employees'); // ✅ Replace with your real table name

    // Check if a valid sys_id was passed as input
    if (inputs.employeeRecord && gr.get(inputs.employeeRecord)) {
        gs.info("Before increment, current_load = " + gr.current_load);

        // Safely parse current_load and increment it
        var currentLoad = parseInt(gr.current_load || 0, 10);
        gr.current_load = currentLoad + 1;

        // Save the record
        gr.update();

        gs.info("After increment, current_load = " + gr.current_load);

        // Set the sys_id of the updated record as output
        outputs.updatedRecord = gr.sys_id;
    } else {
        gs.error("Invalid input: employeeRecord sys_id not found.");
        outputs.updatedRecord = ''; // To avoid publish errors
    }

})(inputs, outputs);

